import React, {useState, useEffect} from 'react';
import axios from 'axios'
import {Link} from 'react-router-dom'


const DisplayAllCustomers = (props) => {
    const{allCustomers, setAllCustomers} = props

    useEffect(() => {
        axios.get('http://localhost:8000/api/allCustomers')
            .then((allCustomers) => {
                console.log(allCustomers);
                setAllCustomers(allCustomers.data)
            })
            .catch((err) => {
                console.log(err);
            })
    })

    return(
        <div>
            <h2 className='text-dark'>CMS Corp <p>We Come to you!</p></h2>

            {
                allCustomers.map((customer) => (
                    <div key={customer._id}>

                <table class=" card-body w-75 mx-auto table-bordered text-dark table-hover table-striped-columns">
                    <thead>
                        <tr>
                            <th>Service Type</th>
                            <th>Customer Name</th>
                            <th>Location</th>
                            <th>Service Status</th>
                        </tr>
                    </thead>
                    <tbody scope='row' className=''>
                        <tr>
                            <td>{customer.typeService}</td>
                            <td>{customer.customerName}</td>
                            <td>{customer.location}</td>
                            <td><Link to={`/oneCustomer/${customer._id}`}>View</Link></td>
                        </tr>
                    </tbody>



                    </table>
                </div>


            ))
        }
        </div>
    )
}

export default DisplayAllCustomers;
