import React, {useState, useEffect} from 'react';
import axios from 'axios'
import {Link} from 'react-router-dom'


const DisplayAllCustomers = (props) => {
    const{allCustomers, setAllCustomers} = props

    useEffect(() => {
        axios.get('http://localhost:8000/api/allCustomers')
            .then((allCustomers) => {
                console.log(allCustomers);
                setAllCustomers(allCustomers.data)
            })
            .catch((err) => {
                console.log(err);
            })
    })

    return(
        <div>
            <h2 className='text-dark'>CMS Corp <p>We Come to you!</p></h2>

            {
                allCustomers.map((customer) => (
                    <div className='card justify-content-around' key={customer._id}>

                        <div className='car-body'>

                        <h5 className='card-title'>{customer.typeService}:</h5>
                        <p className='card-text'>{customer.customerName}</p>
                        <p className='card-text'>{customer.location}</p>
                    </div>




                </div>


            ))
        }
        </div>
    )
}

export default DisplayAllCustomers;
