import React, {useState, useEffect} from 'react';
import axios from 'axios'
import {useNavigate, Link, useParams} from 'react-router-dom'
import { updateCustomer } from '../../../server/controllers/customer.controller';

const UpdateCustomer = (props) => {
    const { id } = useParams();

    const [customerName, setCustomerName] = useState("");
    const [location, setLocation] = useState("");
    const [description, setDescription] = useState("");
    const [typeService, setTypeService] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    consr [errors, setErrors] = useS

    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:8000/api/oneCustomer/${id}`)
            .then(res => {
                setCustomerName(res.data.customerName);
                setLocation(res.data.location);
                setDescription(res.data.description);
                setTypeService(res.data.typeService);
                setPhoneNumber(res.data.phoneNumber);

            })
            .catch(err => console.log(err))
    }, [id])

    const updateCustomer = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:8000/api/updateCustomer/${id}`,{
            customerName,
            location,
            description,
            typeService,
            phoneNumber
        })
            .then(res => {
                console.log(res);
                navigate("/")
            })
            .catch(err => {
                console.log(err);
                setErrors(err.response.data.errors);

            })
    }


    return (
        <div className='d-flex justify-content-evenly'>

            <h1 className=''>Edit {customerName}'s Service</h1>
            <form className='w-100 row g-2 align-items-center text-dark' onSubmit={updateCustomer}>
