import React, { useState, useEffect} from 'react';
import axios from 'axios'
import { useNavigate, useParams, Link } from 'react-router-dom'

const OneCustomer = (props) => {
    const {id} = useParams()
    const [customer,setCustomer] = useState({})
    const navigate = useNavigate()

    useEffect(() => {
        axios.get(`http://localhost:8000/api/oneCustomer/${id}`)
            .then((res) => {
                console.log(res.data);
                setCustomer(res.data)
            })
            .catch((err) => {
                console.log(err);
            })
    }, [id])

    const deleteCustomer = () => {
        axios.delete(`http://localhost:8000/api/deleteCustomer/${id}`)
        .then((res) => {
            console.log(res);
            navigate ("/")
        })
    }

    return (
        <div className="card width: 18rem;">
    <div className="card-body">
        <h5 className="card-title">{customer.customerName}</h5>
        <h6 className="card-subtitle mb-2 text-muted">Service{customer.typeService}</h6>
        <p className="card-text">{customer.description}</p>
        <p className="card-text">{customer.phoneNumber}</p>
        <button onClick={deleteCustomer} className="btn btn-danger">Cancel Service</button>
        <Link to={'/'}>Go Home </Link>
    </div>
</div>
    )


}


export default OneCustomer;
